<!DOCTYPE html><html lang="en"><head>	<meta charset="utf-8">	<meta name="viewport" content="initial-scale=1, width=device-width, user-scalable=1.0, minimum-scale=1.0, maximum-scale=1.0">	<meta name="msapplication-tap-highlight" content="no">	<title>Efiempresa</title>	<link href="../Include/css/materialdesignicons.min.css" media="all" rel="stylesheet" type="text/css" />	<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/materialize/1.0.0-beta/css/materialize.min.css"></head><body>	<div class="navbar-fixed">		<nav>			<div class="nav-wrapper blue lighten-2">				<a href="#!" class="brand-logo"><img src="../Include/img/Efiempresa-logo.pn" alt=""></a>				<a href="#" data-target="mobile" class="sidenav-trigger"><i class="mdi mdi-menu mdi-18px"></i></a>				<ul class="right hide-on-med-and-down">					<li><a class="dropdown-trigger" data-target="opciones_facturas">Facturas</a></li>					<li><a id="clientes">Clientes</a></li>					<li><a id="misdatos">Mis Datos</a></li>					<li><a id="salir"><span class="mdi mdi-power mdi-24px"></span></a></li>				</ul>			</div>		</nav>	</div>	<ul id="opciones_facturas" class="dropdown-content">		<li><a id="crearfactura">Crear</a></li>		<li><a id="historialfactura">Historial</a></li>	</ul>	<ul id="m_opciones_facturas" class="dropdown-content">		<li><a id="m_crearfactura">Crear</a></li>		<li><a id="m_historialfactura">Historial</a></li>	</ul>	<ul class="sidenav grey lighten-2" id="mobile">		<li><a class="dropdown-trigger" data-target="m_opciones_facturas"><i class="mdi mdi-file-document-box"></i>Facturas</a></li>		<li><a id="m_clientes"><i class="mdi mdi-account-switch"></i>Clientes</a></li>		<li><a id="m_misdatos"><i class="mdi mdi-settings-outline"></i>Mis Datos</a></li>		<li><a id="m_salir"><i class="mdi mdi-power"></i>Salir</a></li>	</ul>	<div class="container">		<div id="contenedor">		</div>	</div>	<script src="https://ajax.googleapis.com/ajax/libs/jquery/3.1.1/jquery.min.js"></script>	<script src="https://cdnjs.cloudflare.com/ajax/libs/materialize/1.0.0-beta/js/materialize.min.js"></script>	<script src="../Include/js/tabla.js"></script>	<script type="text/javascript">		$(document).ready(function(){			$(".dropdown-trigger").dropdown();			$('.sidenav').sidenav();			$('#misdatos').click(function(){				var component = 'misdetalles.php';				$.ajax({					mimeType: 'text/html; charset=utf-8', 					url: component,					type: 'GET',					dataType: "html",					async: true,					success: function(data) {						$('#contenedor').html(data);					},					error: function (jqXHR, textStatus, errorThrown) {						alert(errorThrown);					}				});			});			$('#crearfactura').click(function(){				var component = 'invoice.php';				$.ajax({					mimeType: 'text/html; charset=utf-8', 					url: component,					type: 'GET',					dataType: "html",					async: true,					success: function(data) {						$('#contenedor').html(data);					},					error: function (jqXHR, textStatus, errorThrown) {						alert(errorThrown);					}				});			});			$('#m_crearfactura').click(function(){				var component = 'invoice.php';				$.ajax({					mimeType: 'text/html; charset=utf-8', 					url: component,					type: 'GET',					dataType: "html",					async: true,					success: function(data) {						$('#contenedor').html(data);					},					error: function (jqXHR, textStatus, errorThrown) {						alert(errorThrown);					}				});			});		});	</script></body></body></html>